mapper.select:
SELECT * FROM ( SELECT T1.MAIN_BOARD_SEQ ,T1.BOARD_ID ,T1.REG_ID ,T1.HIT_COUNT ,T1.TITLE ,CHAR(T1.SHOW_DATE,ISO) AS SHOW_DATE ,T1.REG_DATE ,T1.CONTENTS ,T1.IS_USE ,T2.MAIN_BOARD_FILE_SEQ ,T2.FILE_SAVENM ,T2.FILE_PATH ,ROW_NUMBER() OVER( ORDER BY T1.SHOW_DATE DESC, T1.MAIN_BOARD_SEQ DESC) AS RNUM FROM HPCDTA.MAIN_BOARD T1 LEFT OUTER JOIN HPCDTA.MAIN_BOARD_FILE T2 ON T1.MAIN_BOARD_SEQ = T2.MAIN_BOARD_SEQ WHERE 1=1
AND BOARD_ID IN
#{type}
AND BOARD_ID=#{boardID}
AND T1.IS_USE = 'Y'
AND (T1.TITLE LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%' OR T1.CONTENTS LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (T1.TITLE LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (T1.CONTENTS LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND T1.SHOW_DATE >= CAST(#{searchFromDate} AS DATE)
AND T1.SHOW_DATE <= CAST(#{searchToDate} AS DATE)
)T WHERE RNUM BETWEEN ((#{pageIndex}-1) * #{pageCount}) + 1 AND ((#{pageIndex}-1) * #{pageCount}) + #{pageCount}

mapper.insert:
     	
			SELECT NEXTVAL FOR HPCDTA.MAIN_BOARD_T_SEQ FROM SYSIBM.SYSDUMMY1
		 
          INSERT INTO 
          	HPCDTA.MAIN_BOARD ( 
				MAIN_BOARD_SEQ
				,BOARD_ID
				,REG_ID
				,HIT_COUNT
				,TITLE
				
					,CODE_SEQ
								
				,SHOW_DATE				
				,REG_DATE
				,CONTENTS
				,IS_USE
          ) VALUES ( 
                #{mainBoardSeq,jdbcType=INTEGER}
				,#{boardID,jdbcType=INTEGER}
				,#{regID,jdbcType=VARCHAR}
				,0
				,#{title,jdbcType=VARCHAR}
				
					,#{codeSeq,jdbcType=INTEGER}
				
				,CAST(#{showDate,jdbcType=DATE} AS DATE)				
				,CURRENT TIMESTAMP
				,#{contents,jdbcType=CLOB}
				,#{isUse,jdbcType=VARCHAR}
          )

mapper.update:
UPDATE HPCDTA.MAIN_BOARD SET
	TITLE=#{title,jdbcType=VARCHAR}
	
		,CODE_SEQ = #{codeSeq,jdbcType=INTEGER}
	
	,SHOW_DATE=CAST(#{showDate,jdbcType=DATE} AS DATE)
	,CONTENTS=#{contents,jdbcType=CLOB}			
	,IS_USE=#{isUse,jdbcType=VARCHAR}
WHERE MAIN_BOARD_SEQ = #{mainBoardSeq,jdbcType=INTEGER}

mapper.insert:
          INSERT INTO 
          	HPCDTA.MAIN_BOARD_FILE (
				MAIN_BOARD_FILE_SEQ
				,MAIN_BOARD_SEQ
				,FILE_TYPE
				,FILE_ORGNM
				,FILE_SAVENM
				,FILE_PATH
				,FILE_SIZE
				,FILE_FORM_NAME
          ) VALUES ( 
                NEXTVAL FOR HPCDTA.MAIN_BOARD_FILE_T_SEQ
				,#{mainBoardSeq,jdbcType=INTEGER}
				,#{fileType,jdbcType=VARCHAR}								
				,#{fileOrgnm,jdbcType=VARCHAR}
				,#{fileSavenm,jdbcType=VARCHAR}
				,#{filePath,jdbcType=VARCHAR}
				,#{fileSize,jdbcType=VARCHAR}
				,#{fileFormName,jdbcType=VARCHAR}
          )

mapper.update:
UPDATE HPCDTA.MAIN_BOARD SET
	HIT_COUNT=HIT_COUNT+1			
WHERE MAIN_BOARD_SEQ = #{mainBoardSeq,jdbcType=INTEGER}

mapper.select:
SELECT * FROM ( SELECT T1.MAIN_BOARD_SEQ ,T1.BOARD_ID ,T1.REG_ID ,T1.HIT_COUNT ,T1.TITLE ,CHAR(T1.SHOW_DATE,ISO) AS SHOW_DATE ,T1.REG_DATE ,T1.CONTENTS ,T1.IS_USE ,T1.CODE_SEQ ,T2.CODE_NAME ,ROW_NUMBER() OVER( ORDER BY T1.SHOW_DATE DESC, T1.MAIN_BOARD_SEQ DESC) AS RNUM FROM HPCDTA.MAIN_BOARD T1 LEFT OUTER JOIN HPCDTA.COMMCODE T2 ON T1.CODE_SEQ = T2.CODE_SEQ WHERE BOARD_ID=#{boardID}
AND T1.IS_USE = 'Y'
AND (T1.TITLE LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%' OR T1.CONTENTS LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (T1.TITLE LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (T1.CONTENTS LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (T1.CODE_SEQ = #{codeSeq})
AND T1.SHOW_DATE >= CAST(#{searchFromDate} AS DATE)
AND T1.SHOW_DATE <= CAST(#{searchToDate} AS DATE)
)T WHERE RNUM BETWEEN ((#{pageIndex}-1) * #{pageCount}) + 1 AND ((#{pageIndex}-1) * #{pageCount}) + #{pageCount}
