mapper.insert:
INSERT INTO
	HPCDTA.ADM_LOG
(
ADM_LOG_SEQ
,ADM_ID
,ADM_NAME
,REG_DATE
,IP
,LOGIN_YN
)
VALUES (
NEXTVAL FOR HPCDTA.ADM_LOG_T_SEQ
, #{admID}
, #{admName}	
, CURRENT TIMESTAMP
, #{ip}
, #{loginYN}
)

mapper.select:
SELECT * FROM( SELECT ADM_LOG_SEQ , ADM_ID , ADM_NAME , REG_DATE , IP , LOGIN_YN , ROW_NUMBER() OVER( ORDER BY REG_DATE DESC, ADM_ID DESC) AS RNUM FROM HPCDTA.ADM_LOG WHERE 1=1
AND (ADM_ID LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (ADM_NAME LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
AND (IP LIKE '%'||TRIM(CAST ((#{searchText}) AS VARCHAR(100) CCSID 1208))||'%')
)T WHERE RNUM BETWEEN ((#{pageIndex}-1) * #{pageCount}) + 1 AND ((#{pageIndex}-1) * #{pageCount}) + #{pageCount}

mapper.insert:
INSERT INTO
	HPCDTA.USER_LOG
(
SESSION_ID
,IP		
,INT_DATE
,REG_DATE
,REFERER_URL		
)
VALUES (
#{sessionID,jdbcType=VARCHAR}
, #{ip,jdbcType=VARCHAR}		
, INTEGER(REPLACE(CHAR(CURRENT DATE,ISO),'-',''))
, CURRENT TIMESTAMP
, #{refererUrl,jdbcType=VARCHAR}		
)

mapper.select:
SELECT INT_DATE, COUNT(INT_DATE) as USER_CNT FROM HPCDTA.USER_LOG WHERE 1=1
AND INT_DATE >= #{intFromDate}
AND INT_DATE <= #{intToDate}
GROUP BY INT_DATE ORDER BY INT_DATE DESC
